        var fromValue = { city: $.trim($("#" + this.CityInputConfig.FromCityId).val()), lke: $("#" + this.CityInputConfig.FromCityId + "LKE").val() };
        var toValue = { city: $.trim($("#" + this.CityInputConfig.ToCityId).val()), lke: $("#" + this.CityInputConfig.ToCityId + "LKE").val() };

