        var isFrom = false;
        var isTo = false;

        if (fromValue.lke == "") { alert("请输入出发城市"); return false; }
        if (toValue.lke == "") { alert("请输入目的城市"); return false; }
        if (leaveTime == "") { alert("请输入出发时间"); return false; }

        //返程
        if (VoyageType == 2) {
            if (EndTimte == "") {
                alert("请输入返程时间"); return false;
            } else {
                if (EndTimte < leaveTime) {
                    alert("返程时间不能小于出发时间!"); return false;
                }
            }
        }

