            if (ticketLKEdata[i].lke == fromValue.lke) {
                if (ticketLKEdata[i].cName == fromValue.city) { isFrom = true; }
            }

            if (ticketLKEdata[i].lke == toValue.lke) {
                if (ticketLKEdata[i].cName == toValue.city) { isTo = true; }
            }

