    //异步提交机票信息查询数据
    ajaxPost: function() {
        //表单验证
        var formValidateResult = this.webFormSubmit();
        if (!formValidateResult) { return false; }
        //取表单值
        var formValue = { VoyageType: $("input[name='radVoyageType']:checked").val(), flke: $("#" + this.CityInputConfig.FromCityId + "LKE").val(), tlke: $("#" + this.CityInputConfig.ToCityId + "LKE").val(), startTime: $("#" + this.TimeDateConfig.dateTimeControlId).val(), endTime: $("#" + this.TimeDateConfig.EnddateTimeControlId).val() };

        //提交数据
        $.ajax({
            type: "GET",
            url: ticketLKE.stringPort + "/PlaneInfo/DefaultPost.ashx?callback=?&" + $.param(formValue),
            dataType: 'jsonp',
            success: function(result) {
                if (result.IsSucceed) {
                    window.open(result.RedirectUrl);
                } else {
                    alert(result.ErrorMsg);
                    return false;
                }
            }
        });
    },
