    hiddenDiv: function() {
        $(document).click(function(e) {
            if (document.all) e = event;
            var ele = e.target == undefined ? event.srcElement : e.target;
            if (ele.id != "fromCity" && ele.id != "toCity") {
                ticketLKE.hideHotCitys();
            }
        });
    },
