        //if (!formValidateResult) { return false; }
        //取表单值
        var formValue = { VoyageType:"", flke: "", tlke: "", startTime: "", endTime: "" };
        var GogoUrl = ticketLKE.stringPort + "/PlaneInfo/DefaultPost.ashx?" + $.param(formValue) + "&isFromMQ=1";
        window.open(GetDesPlatformUrlForMQMsg(GogoUrl, MqId, PassWord));
