INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select 'CC3C1311-BE5A-4D95-A5C5-46CA2AF34188','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='生日祝福'),'盈盈今日天如水，恋恋当年水似天。情缘驻我心，相思比梦长。祝福你生日快乐！生命中的每一天永远青春靓丽！','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '2F49AEB6-AFE3-4872-B9E7-EEF4D8B46765','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='生日祝福'),'在你生日来临之即，祝事业正当午，身体壮如虎，金钱不胜数，干活不辛苦，悠闲像老鼠，浪漫似乐谱，快乐非你莫属！','2010-09-27 12:08:39'
