INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '82D54F91-13C4-4FAA-9391-40DD04295115','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='儿童节'),'听你妈妈说你小时候从来没有尿过床，是一个典型的乖孩子，这么光荣的事情怎么可以埋没？来我教你怎么发扬光大，走到大街上，随便找一个阿姨，对她说：“阿姨，我好乖，我没有尿床……”哈哈，儿童节快乐。','2010-09-27 12:08:39'
