INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '94295D32-A8EB-4BE2-8AF5-985268218A51','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='儿童节'),'和儿童在一起，会使人变得单纯，变得年轻，变得富有爱心，富有灵感。难怪我变了，原来是因为和你在一起，呵呵，祝你六一儿童节快乐！','2010-09-27 12:08:39'
