INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '82E370D7-6EC2-4455-8078-F1C67E486C48','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'八月十五将至，送你一只月饼，含量：100%关心，配料：甜蜜+快乐+开心+宽容+忠诚=幸福；保质期：一辈子；保存方法：珍惜。','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '4890FF80-157F-4B84-9831-4F89701FA558','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'八月中秋不送礼，发条短信祝福你，健康快乐长伴你，好运和你不分离，还有让我告诉你，财神已经跟随你。','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select 'C464D9DE-BB1C-481B-A62C-CE93BEECCA2F','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'八月中秋月儿圆，对着嫦娥表思念，心中的人啊你可看见，嫦娥也在默默祝愿，愿你生活比蜜甜！','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '65946A78-82C3-4225-9E57-AFBE117118C4','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'一个萝卜两片藕，幸福与你手牵手；三两豆腐四片姜，观物你宜放眼量；五个月饼六块糖，平等沟通团圆有。','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '315560F6-05FC-45E7-952F-A34092329227','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'又是一年月圆夜，月下为你许三愿：一愿美梦好似月儿圆，二愿日子更比月饼甜，三愿美貌犹如月中仙。','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select 'B303B800-2D7B-4165-9AA8-E56E7583EAA0','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'愿你过的每一天都象十五的月亮一样成功圆满。','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '009FD15E-8F65-4CD1-B201-E05864299E76','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'把酝酿已久的创意交给秋风，任其去演绎相思的旋律；让翘盼团聚的心跃上太空，在月宫桂树下再叙永恒的主题。','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '33D3EF0C-71D4-4985-9326-997D592D9F10','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'春有百花秋望月，夏有凉风冬听雪，心中若无烦恼事，便是人生好时节，愿你晨有清逸暮有悠闲，梦随心动心随梦求，祝中秋节愉快！','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select 'C788EACE-0FED-41AD-8833-493A318B8223','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'风柔雨润花好月圆，幸福生活好甜！冬去春来似水如烟，一年中秋在眼前！流年不复返，人生须尽欢。中秋快乐！','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '93996FC0-8006-4B26-A3C4-B4D6D7DB58AC','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'嗨，嫦娥让我给你带个话，今年中秋给她留点月饼渣，她明年会给你送个大金月饼。祝中秋节快乐！','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select 'BBAE4F42-2441-42C7-9D10-A68BC58601BE','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'后羿忙射日，嫦娥偷奔月。月老不曾老，自古情难了。中秋月儿挂，心中圆往事。玉兔伴嫦娥，绢带千古绕相思。','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '327BEE7A-FEDC-47D7-A040-359FBE68C590','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'佳节到，送您个月饼：第一层财运；第二层幸运；第三层福运；第四层浪漫；中间夹层甜蜜，祝您天天好心情！','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select 'A09BE4E5-D310-4862-A364-9B12676B3D18','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'金秋圆月挂天边，浪子心中思念添；寂寞嫦娥空际舞，八戒被贬为那般；我予汝心天可表，千里也能共婵娟。','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '44E1061A-DF24-460E-9C01-70DFAB130DFA','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'明月，一闪一闪，挂天边；思念，一丝一丝，连成线；回忆，一幕一幕，在眼前；但愿，一年一年，人圆全。','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select 'A4EB1418-1AAF-4841-91B8-59C92993348B','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'明月本无价，高山皆有情。路遥千里，难断相思。人虽不至，心向往之。衷心祝愿您和家人团圆美满,幸福安康！','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '198042B9-9791-49C5-866B-24276CD9EB99','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'明月几时有，把饼问青天，不知饼中何馅，今日是莲蓉，我欲乘舟观月，又恐飞船太慢，远处不胜寒。','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '6924E410-234F-4E30-BE23-2FDE75D9C802','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'明月千里难以触摸，对你的思念无处寄托，举杯邀明月，说：福多！钱多！快乐多！','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select 'F3F40A8E-9717-4CBF-9F39-CCC0863287BC','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'明月清风，风送祝福；把酒对月，月表人圆；皓月当空，空气传情；道是有缘，缘自月圆！','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '9ABB09A2-72B2-4CE8-ABFF-587F4AA42C5D','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'你是月饼我是馅，缠缠绵绵总见面；你是风筝我是线，追追逐逐把你牵；你是明月我是泉，美美满满一万年！','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select 'C07AC2A8-0802-4E9C-B88C-050AC75066E1','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'年年圆满如意，月月事事顺心，日日喜悦无忧，时时高兴欢喜，刻刻充满朝气，月圆人圆花好，事顺业顺家兴！','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select 'C001CA60-9BA9-46EE-B19E-D9D209337D10','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'朋友，不管你现在身处何方，今晚一定要看看那被你遗忘已久的月亮，许个愿吧，很可能就会实现哦!中秋快乐！','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select 'E26123C8-2643-4119-803A-9D3C530580E1','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'千里试问平安否？且把思念遥相寄。绵绵爱意与关怀，浓浓情意与祝福，中秋快乐！','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '80EF4B36-E632-4C9F-88FF-61087394B019','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'清风带去我的祝福，明月捎走我的问候，送你一丝风儿，赠你一缕月儿，风月无边，人间有情，中秋快乐！','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '7F68E8C8-76ED-416A-BE49-F6D129CC445A','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'秋江潮水连海平，海上明月共潮生，花好月圆人团聚，祝福声声伴你行。祝你中秋愉快,靓丽一生!','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '530DF4B5-88FD-4761-B354-ED7A2E2741F1','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'去年明月今又来，人间一岁几多情，但愿月圆花常开，世间此情古今同。','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '3A1F7D0D-FABE-4C50-9AAE-9062526D33C2','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'去年圆月时，花灯亮如昼；月上柳梢头，人约黄昏后。今年圆月时，月与灯依旧；不见去年人，泪湿春衫袖。','2010-09-27 12:08:39'
