INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select 'FCC6542B-4A5C-4809-8DEC-9ECA1EE6642C','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'三五夜中新月色，二千里外故人心。今夜月明人尽望，不知秋思落谁家。','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '57D70966-86DC-4B73-BEF3-9B9D98C47690','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'十五月，圆又圆，打制的月饼甜又甜，嫦娥仙女来做馅，玉兔宝宝忙做皮，月洒银辉做封面，星光点点做提绳。','2010-09-27 12:08:39'
