INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select 'C861C545-AF60-4FB2-8981-2A4BB315FB64','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'网缘！情缘！月圆！中秋夜语寄相思，花好月圆情难圆。带去问候和思恋，心想事成愿缘圆。','2010-09-27 12:08:39'
INSERT INTO SMS_CommonWords([ID],[CompanyID],[UserID],[ClassID],[WordContent],[IssueTime]) select '19411E41-9175-4EE9-AD7B-53CE8DF17617','','',(select [ID] from [SMS_CommonWordClass] where companyid='' and userid='' and classname='中秋节祝福'),'我正着手筹备公司生产月饼：用浪漫做皮；用温馨做馅；幸福做蛋王。懂得品味的人不需要多，最好只有你一个！','2010-09-27 12:08:39'
